import { CodeOutlined, LockOutlined, UserOutlined } from '@ant-design/icons';
import {
  LoginFormPage,
  ProFormCheckbox,
  ProFormText,
} from '@ant-design/pro-components';
import { Image, message, Spin } from 'antd';
import { Link, useModel } from '@umijs/max';
import { useRequest, useTitle } from 'ahooks';
import { authLogin, getPublicAuthCode } from '@/services';
import { TOKEN } from '@/constants';
import storage from '@/utils/storage';
import logo from '@/assets/parsec-logo.svg';
import NProgress from 'nprogress';

export default () => {
  useTitle('秒差距中后台管理系统-登录');
  const { initialState, setInitialState } = useModel('@@initialState');
  const { loading, data, refresh } = useRequest<API.AuthCodeVo, any>(
    getPublicAuthCode,
    {
      refreshOnWindowFocus: true,
      onFinally: () => NProgress.done(),
    },
  );

  const fetchUserInfo = async () => {
    const userInfo = await initialState?.fetchUserInfo?.();
    if (userInfo) {
      await setInitialState((s) => ({
        ...s,
        currentUser: userInfo,
      }));
    }
  };

  const handleLogin = async (formData: any) => {
    const loginRes = await authLogin({
      ...formData,
      clientCode: data?.clientCode,
    }).catch(() => {
      refresh(); // 刷新图片验证码
    });
    // TODO: 模拟登录正式请删除
    console.log('loginRes', loginRes);
    storage.set(TOKEN, loginRes?.data || 'eyJhbGciOiJIUzUxMiIsInppcCI6IkdaSVAifQ.H4sIAAAAAAAAAKtWyiwuVrJScszJTUysUtJRSixNAXINgazi0iSQhIuvpx-Ql1pRoGRlaGZmbmlhbGpqUAsAX7_vLzkAAAA.68vkT7Pf2-N0maYVuVvUF_I4-R_sTmAcGjtmzBSrpSXj6_D4qOzc57StHZBMEPhuJS-fQ6atVA8Pv6na6qHJmQ');
    window.location.replace('/');
    return;
    if (loginRes) {
      if (loginRes?.code === 0) {
        storage.set(TOKEN, loginRes?.data || '');
        message.success('登录成功！');
        await fetchUserInfo();
        const urlParams = new URL(window.location.href).searchParams;
        if (
          !!urlParams.get('redirect') &&
          !urlParams.get('redirect')?.endsWith('/login')
        ) {
          window.location.replace(urlParams.get('redirect') || '');
        } else {
          window.location.replace('/');
        }
      } else {
        message.error(loginRes?.message || '登录失败！');
      }
    }
    return;
  };
  return (
    <div style={{ backgroundColor: 'white', height: '100vh' }}>
      <LoginFormPage
        backgroundImageUrl={'https://gw.alipayobjects.com/zos/rmsportal/FfdJeJRQWjEeGTpqgBKj.png'}
        logo={logo}
        title={(<span>HI~</span>) as any}
        subTitle="欢迎使用秒差距中后台管理系统"
        onFinish={handleLogin}
      >
        <ProFormText
          name="username"
          fieldProps={{
            size: 'large',
            prefix: <UserOutlined className={'prefixIcon'} />,
          }}
          placeholder={'请输入用户名'}
          rules={[
            {
              required: true,
              message: '请输入用户名!',
            },
          ]}
        />
        <ProFormText.Password
          name="password"
          fieldProps={{
            size: 'large',
            prefix: <LockOutlined className={'prefixIcon'} />,
          }}
          placeholder={'请输入密码'}
          rules={[
            {
              required: true,
              message: '请输入密码！',
            },
          ]}
        />
        <ProFormText
          name="captchaCode"
          fieldProps={{
            size: 'large',
            prefix: <CodeOutlined className={'prefixIcon'} />,
            suffix: (
              <Spin spinning={loading}>
                <Image
                  onClick={() => refresh()}
                  width={100}
                  height={26}
                  preview={false}
                  src={`${APP_API_HOST}${data?.captchaImageUrl}`}
                  style={{ cursor: 'pointer' }}
                  fallback="data:image/png;base64,"
                />
              </Spin>
            ),
          }}
          placeholder={'请输入验证码'}
          rules={[
            {
              required: true,
              message: '请输入验证码!',
            },
          ]}
        />
        <div
          style={{
            marginBlockEnd: 24,
          }}
        >
          <ProFormCheckbox noStyle name="autoLogin">
            自动登录
          </ProFormCheckbox>
          <Link
            to={'/auth/forget-password'}
            style={{
              float: 'right',
            }}
          >
            忘记密码
          </Link>
        </div>
      </LoginFormPage>
    </div>
  );
};
